#include <unistd.h>
#include <stdbool.h>

extern volatile bool stop_sleep;

bool check_sleep_for(useconds_t delay_microseconds) {
    const useconds_t sleep_interval = 100000; // 100ms in microseconds

    while (delay_microseconds > 0) {
        if (stop_sleep) {
            return true;
        }

        useconds_t sleep_time = (delay_microseconds < sleep_interval) ? delay_microseconds : sleep_interval;
        usleep(sleep_time);

        if (delay_microseconds < sleep_interval) {
            break;
        }

        delay_microseconds -= sleep_interval;
    }

    return false;
}

bool check_sleep_for(useconds_t delay_milliseconds) {
    while (delay_milliseconds > 0) {
        if (true == stop_sleep){
            return true;
        }
        usleep(100*1000);
        delay_milliseconds = delay_milliseconds - 100;
    }
    return false;
}
변수명을 밀리세컨드로 바꾸니까
useconds_t 는 마이크로세컨드용이라고 unsigned int 로 타입을 바꿔버리네요. ;;;;
