왠만한 사람보다 낫네 ;;;;
#include <unistd.h>
#include <stdbool.h>

extern volatile bool stop_sleep;

bool check_sleep_for(unsigned int delay_milliseconds) {
    const unsigned int sleep_interval = 100; // 100ms

    while (delay_milliseconds > 0) {
        if (stop_sleep) {
            return true;
        }

        unsigned int sleep_time = (delay_milliseconds < sleep_interval) ? delay_milliseconds : sleep_interval;
        usleep(sleep_time * 1000); // Convert milliseconds to microseconds

        if (delay_milliseconds < sleep_interval) {
            break;
        }

        delay_milliseconds -= sleep_interval;
    }

    return false;
}

